package example;
import java.util.*;
public class kmTomile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		System.out.print("Enter the amount in KiloMeters :");
		int km=sc.nextInt();
		System.out.print("Converted into miles : "+ km*(0.621));

	}

}
